(function () {

  /* ============================================================
   * Product Image – "Learn More" opens product in modal
   *
   * The HTML generated by GenerateBlocks looks like:
   *   .gb-loop-item.post-{ID}  → contains the card
   *     .wp-block-button__link  → "Learn More" button (no href)
   *
   * We intercept clicks on .product-image "Learn More" buttons,
   * grab the post ID from the parent .gb-loop-item class list,
   * then reuse the existing use-case modal + AJAX system.
   * ============================================================ */

  document.addEventListener('click', function (e) {

    // Only act on buttons inside a .product-image block
    const btn = e.target.closest('.product-image .wp-block-button__link');
    if (!btn) return;

    e.preventDefault();

    // Walk up to the gb-loop-item to get the post ID
    const loopItem = btn.closest('.gb-loop-item');
    if (!loopItem) return;

    const postClass = Array.from(loopItem.classList).find(c => c.startsWith('post-') && /^post-\d+$/.test(c));
    if (!postClass) return;

    const postId = postClass.replace('post-', '');
    if (!postId) return;

    // Reuse the existing use-case modal DOM
    const modal = document.getElementById('use-case-modal');
    const body  = document.getElementById('use-case-modal-body');
    if (!modal || !body) return;

    modal.classList.remove('hidden');
    document.body.classList.add('modal-open');
    body.innerHTML = '<p style="padding:40px;text-align:center;">Loading…</p>';

    fetch(UseCasesAjax.ajaxurl, {
      method: 'POST',
      headers: { 'Content-Type': 'application/x-www-form-urlencoded' },
      body: new URLSearchParams({
        action: 'load_product_modal',
        post_id: postId
      })
    })
    .then(r => r.text())
    .then(html => {
      body.innerHTML = html || '<p>No content found.</p>';
    })
    .catch(() => {
      body.innerHTML = '<p>Error loading content.</p>';
    });

  });

})();
